import sys
from pathlib import Path

repo = Path.cwd() / 'agent'
if not repo.is_dir():
    raise SystemExit('Could not find repo root with agent/ directory. Run from repo root.')

sys.path.insert(0, str(Path.cwd()))
from agent.integrations import yahoo_mail

items = yahoo_mail.list_messages(limit=5, folder='INBOX')
if not items:
    print('No messages found or unable to read inbox.')
else:
    for i, msg in enumerate(items, 1):
        print(f"{i}. {msg.get('date','')} | {msg.get('from','')} | {msg.get('subject','')}")

import sys
from pathlib import Path

sys.path.insert(0, str(Path.cwd()))
from agent.integrations import google_apis

print('\n=== Testing Google Tasks API ===')
try:
    tasks = google_apis.list_tasks(max_results=5)
    if tasks:
        print(f'✓ Found {len(tasks)} tasks:')
        for i, task in enumerate(tasks, 1):
            print(f"  {i}. {task.get('title', 'Untitled')}")
    else:
        print('✓ Connected (no tasks found)')
except Exception as e:
    print(f'✗ Error: {e}')

