import sys
from pathlib import Path

repo = Path.cwd() / 'agent'
if not repo.is_dir():
    raise SystemExit('Could not find repo root with agent/ directory. Run from repo root.')

sys.path.insert(0, str(Path.cwd()))
from agent.integrations import yahoo_mail

items = yahoo_mail.list_messages(limit=5, folder='INBOX')
if not items:
    print('No messages found or unable to read inbox.')
else:
    for i, msg in enumerate(items, 1):
        print(f"{i}. {msg.get('date','')} | {msg.get('from','')} | {msg.get('subject','')}")
